import React , {useState}  from 'react'
import './الملاعب.css' ;


function Reservation()  {
   
 
    const [reservation , setReserve ]= useState(false) ;
 
   
      return(
        <div className="reservation">
         
         <button onClick= {()=> setReserve(!reservation)     }  >
         احجز من هنا 
         </button>
         
        {
            reservation ?   <form  >
            <div >
            <input type='text' name="fname" placeholder='Enter Your Name'  /> <br></br>
             <br></br>
             
            <input type='text' name="email" placeholder='Enter a valid email address '  /><br></br>
            <br></br>
            
             <textarea name="message" 
              rows="10" cols="60" 
              placeholder= 'Enter your message'
              > 
             </textarea><br></br>
             <br></br>
             
             <button type="submit" className="submit"  > Submit </button>
            </div>
           
             
          </form> : null
             }
          
        
        </div>
    
      ) 
}

export default Reservation ;
